// declarations.d.ts
declare module '*.less' {
  const content: any;
  export default content;
}

declare module '*.css' {
  const content: any;
  export default content;
}

// Lottie 文件类型声明
declare module '*.lottie' {
  const content: any;
  export default content;
}

declare module '*.lottie?inline' {
  const content: any;
  export default content;
}
